alias OsnAiPrep.Repo
alias OsnAiPrep.Mcq.Question

defmodule CVSeeder do
  def insert_all(questions) do
    for q <- questions do
      %Question{}
      |> Question.changeset(q)
      |> Repo.insert!()
    end
    length(questions)
  end
end

