  %{question_en: "What is GAN in computer vision?", question_id: "Apa itu GAN dalam computer vision?", option_a_en: "Generative Adversarial Network - generator and discriminator competing to create realistic images", option_a_id: "Generative Adversarial Network - generator dan discriminator bersaing untuk membuat gambar realistis", option_b_en: "General Area Network", option_b_id: "General Area Network", option_c_en: "A classification network", option_c_id: "Network klasifikasi", option_d_en: "Gaming AI Network", option_d_id: "Network AI Gaming", correct_answer: "A", explanation_en: "Generator creates fake images, discriminator distinguishes real from fake. They improve each other.", explanation_id: "Generator membuat gambar palsu, discriminator membedakan asli dari palsu. Mereka saling memperbaiki.", topic: "computer_vision", difficulty: "medium", competition: "noai_prelim"},
  %{question_en: "What is the discriminator in a GAN?", question_id: "Apa itu discriminator dalam GAN?", option_a_en: "A network that classifies images as real or fake", option_a_id: "Network yang mengklasifikasi gambar sebagai asli atau palsu", option_b_en: "A network that generates images", option_b_id: "Network yang menghasilkan gambar", option_c_en: "A filtering layer", option_c_id: "Layer filter", option_d_en: "A bias detector", option_d_id: "Detektor bias", correct_answer: "A", explanation_en: "Discriminator tries to correctly identify which images came from the generator vs the real dataset.", explanation_id: "Discriminator berusaha mengidentifikasi dengan benar gambar mana yang berasal dari generator vs dataset asli.", topic: "computer_vision", difficulty: "medium", competition: "noai_prelim"},
  %{question_en: "What is the generator in a GAN?", question_id: "Apa itu generator dalam GAN?", option_a_en: "A network that creates images from random noise", option_a_id: "Network yang membuat gambar dari noise acak", option_b_en: "A network that classifies images", option_b_id: "Network yang mengklasifikasi gambar", option_c_en: "A power generator", option_c_id: "Generator daya", option_d_en: "A data source", option_d_id: "Sumber data", correct_answer: "A", explanation_en: "Generator maps random latent vectors to images, learning to produce realistic samples that fool the discriminator.", explanation_id: "Generator memetakan vektor laten acak ke gambar, belajar menghasilkan sampel realistis yang menipu discriminator.", topic: "computer_vision", difficulty: "medium", competition: "noai_prelim"},
  %{question_en: "What is style transfer?", question_id: "Apa itu style transfer?", option_a_en: "Applying the artistic style of one image to the content of another", option_a_id: "Menerapkan gaya artistik satu gambar ke konten gambar lain", option_b_en: "Copying images", option_b_id: "Menyalin gambar", option_c_en: "Transferring image files", option_c_id: "Mentransfer file gambar", option_d_en: "Changing image format", option_d_id: "Mengubah format gambar", correct_answer: "A", explanation_en: "Style transfer separates content and style, then recombines them. Uses CNN feature matching.", explanation_id: "Style transfer memisahkan konten dan gaya, lalu menggabungkannya kembali. Menggunakan pencocokan fitur CNN.", topic: "computer_vision", difficulty: "medium", competition: "noai_prelim"},
  %{question_en: "What is image inpainting?", question_id: "Apa itu image inpainting?", option_a_en: "Filling in missing or corrupted parts of an image", option_a_id: "Mengisi bagian gambar yang hilang atau rusak", option_b_en: "Painting images", option_b_id: "Melukis gambar", option_c_en: "Adding paint filters", option_c_id: "Menambahkan filter cat", option_d_en: "Image colorization", option_d_id: "Pewarnaan gambar", correct_answer: "A", explanation_en: "Inpainting reconstructs missing regions using surrounding context. Used for photo restoration, object removal.", explanation_id: "Inpainting merekonstruksi region yang hilang menggunakan konteks sekitar. Digunakan untuk restorasi foto, penghapusan objek.", topic: "computer_vision", difficulty: "medium", competition: "noai_prelim"},
  %{question_en: "What is super-resolution?", question_id: "Apa itu super-resolution?", option_a_en: "Increasing image resolution while adding realistic details", option_a_id: "Meningkatkan resolusi gambar sambil menambahkan detail realistis", option_b_en: "Very high resolution cameras", option_b_id: "Kamera resolusi sangat tinggi", option_c_en: "Reducing resolution", option_c_id: "Mengurangi resolusi", option_d_en: "Super fast processing", option_d_id: "Pemrosesan super cepat", correct_answer: "A", explanation_en: "Super-resolution upscales images beyond simple interpolation, hallucinating realistic high-frequency details.", explanation_id: "Super-resolution meningkatkan skala gambar melampaui interpolasi sederhana, menghalusinasi detail frekuensi tinggi yang realistis.", topic: "computer_vision", difficulty: "medium", competition: "noai_prelim"},
  %{question_en: "What is optical flow?", question_id: "Apa itu optical flow?", option_a_en: "Estimating pixel-level motion between consecutive video frames", option_a_id: "Memperkirakan gerakan tingkat pixel antara frame video berturut-turut", option_b_en: "Light flowing through camera", option_b_id: "Cahaya mengalir melalui kamera", option_c_en: "Camera movement", option_c_id: "Gerakan kamera", option_d_en: "Video compression", option_d_id: "Kompresi video", correct_answer: "A", explanation_en: "Optical flow computes dense motion vectors, useful for video understanding, tracking, and action recognition.", explanation_id: "Optical flow menghitung vektor gerakan padat, berguna untuk pemahaman video, tracking, dan pengenalan aksi.", topic: "computer_vision", difficulty: "hard", competition: "noai_prelim"},
  %{question_en: "What is face recognition?", question_id: "Apa itu pengenalan wajah?", option_a_en: "Identifying or verifying a person from their facial features", option_a_id: "Mengidentifikasi atau memverifikasi seseorang dari fitur wajah mereka", option_b_en: "Detecting faces", option_b_id: "Mendeteksi wajah", option_c_en: "Creating faces", option_c_id: "Membuat wajah", option_d_en: "Face filtering", option_d_id: "Memfilter wajah", correct_answer: "A", explanation_en: "Face recognition maps faces to embeddings, then compares embeddings for identification/verification.", explanation_id: "Pengenalan wajah memetakan wajah ke embedding, lalu membandingkan embedding untuk identifikasi/verifikasi.", topic: "computer_vision", difficulty: "easy", competition: "noai_prelim"},
  %{question_en: "What is face detection?", question_id: "Apa itu deteksi wajah?", option_a_en: "Locating and marking faces in an image with bounding boxes", option_a_id: "Menemukan dan menandai wajah dalam gambar dengan bounding box", option_b_en: "Recognizing specific faces", option_b_id: "Mengenali wajah spesifik", option_c_en: "Creating face images", option_c_id: "Membuat gambar wajah", option_d_en: "Face beautification", option_d_id: "Mempercantik wajah", correct_answer: "A", explanation_en: "Face detection finds where faces are located, different from recognition which identifies who they are.", explanation_id: "Deteksi wajah menemukan di mana wajah berada, berbeda dari pengenalan yang mengidentifikasi siapa mereka.", topic: "computer_vision", difficulty: "easy", competition: "noai_prelim"},
  %{question_en: "What is pose estimation?", question_id: "Apa itu estimasi pose?", option_a_en: "Detecting the position and orientation of body parts or objects", option_a_id: "Mendeteksi posisi dan orientasi bagian tubuh atau objek", option_b_en: "Estimating photo quality", option_b_id: "Memperkirakan kualitas foto", option_c_en: "Camera positioning", option_c_id: "Penentuan posisi kamera", option_d_en: "Photo composition", option_d_id: "Komposisi foto", correct_answer: "A", explanation_en: "Pose estimation finds key body joints/landmarks. Used in action recognition, AR, sports analysis.", explanation_id: "Estimasi pose menemukan sendi/landmark tubuh kunci. Digunakan dalam pengenalan aksi, AR, analisis olahraga.", topic: "computer_vision", difficulty: "medium", competition: "noai_prelim"},
  %{question_en: "What is occlusion in computer vision?", question_id: "Apa itu oklusi dalam computer vision?", option_a_en: "When objects or parts of objects are hidden by other objects", option_a_id: "Ketika objek atau bagian objek tersembunyi oleh objek lain", option_b_en: "Image blur", option_b_id: "Blur gambar", option_c_en: "Low lighting", option_c_id: "Pencahayaan rendah", option_d_en: "Camera shake", option_d_id: "Goyangan kamera", correct_answer: "A", explanation_en: "Occlusion is a major challenge: partially visible objects are harder to detect and track.", explanation_id: "Oklusi adalah tantangan utama: objek yang terlihat sebagian lebih sulit untuk dideteksi dan dilacak.", topic: "computer_vision", difficulty: "medium", competition: "noai_prelim"},
  %{question_en: "What is batch normalization in CNNs?", question_id: "Apa itu batch normalization di CNN?", option_a_en: "Normalizing activations across the batch to stabilize training", option_a_id: "Menormalisasi aktivasi di seluruh batch untuk menstabilkan training", option_b_en: "Normalizing batch size", option_b_id: "Menormalisasi ukuran batch", option_c_en: "Batch processing", option_c_id: "Pemrosesan batch", option_d_en: "Normal batch creation", option_d_id: "Pembuatan batch normal", correct_answer: "A", explanation_en: "BatchNorm after conv layers allows higher learning rates and acts as regularization.", explanation_id: "BatchNorm setelah layer conv memungkinkan learning rate lebih tinggi dan bertindak sebagai regularisasi.", topic: "computer_vision", difficulty: "medium", competition: "noai_prelim"},
  %{question_en: "What is a kernel in convolution?", question_id: "Apa itu kernel dalam konvolusi?", option_a_en: "A small matrix of learnable weights applied across the input", option_a_id: "Matriks kecil dari bobot yang dapat dipelajari yang diterapkan di seluruh input", option_b_en: "The core of the network", option_b_id: "Inti dari network", option_c_en: "Operating system kernel", option_c_id: "Kernel sistem operasi", option_d_en: "Seed of an image", option_d_id: "Benih dari gambar", correct_answer: "A", explanation_en: "Kernel (filter) is typically 3x3 or 5x5 weights that slide over input detecting patterns like edges.", explanation_id: "Kernel (filter) biasanya bobot 3x3 atau 5x5 yang meluncur di atas input mendeteksi pola seperti tepi.", topic: "computer_vision", difficulty: "easy", competition: "noai_prelim"},
  %{question_en: "What is the Sobel operator?", question_id: "Apa itu operator Sobel?", option_a_en: "A classical filter for edge detection using gradient approximation", option_a_id: "Filter klasik untuk deteksi tepi menggunakan aproksimasi gradient", option_b_en: "A deep learning layer", option_b_id: "Layer deep learning", option_c_en: "A pooling method", option_c_id: "Metode pooling", option_d_en: "An activation function", option_d_id: "Fungsi aktivasi", correct_answer: "A", explanation_en: "Sobel computes image gradients in x and y directions, highlighting edges. Pre-deep learning technique.", explanation_id: "Sobel menghitung gradient gambar dalam arah x dan y, menyoroti tepi. Teknik pra-deep learning.", topic: "computer_vision", difficulty: "medium", competition: "noai_prelim"},
  %{question_en: "What is SIFT?", question_id: "Apa itu SIFT?", option_a_en: "Scale-Invariant Feature Transform - classical method for feature detection and matching", option_a_id: "Scale-Invariant Feature Transform - metode klasik untuk deteksi dan pencocokan fitur", option_b_en: "A deep learning model", option_b_id: "Model deep learning", option_c_en: "Shifting images", option_c_id: "Menggeser gambar", option_d_en: "Simple image filtering", option_d_id: "Filtering gambar sederhana", correct_answer: "A", explanation_en: "SIFT extracts distinctive local features invariant to scale and rotation. Used before deep learning era.", explanation_id: "SIFT mengekstrak fitur lokal khas yang invarian terhadap skala dan rotasi. Digunakan sebelum era deep learning.", topic: "computer_vision", difficulty: "hard", competition: "noai_prelim"},
  %{question_en: "What is HOG?", question_id: "Apa itu HOG?", option_a_en: "Histogram of Oriented Gradients - feature descriptor for object detection", option_a_id: "Histogram of Oriented Gradients - deskriptor fitur untuk deteksi objek", option_b_en: "High Output Gradient", option_b_id: "High Output Gradient", option_c_en: "A deep learning method", option_c_id: "Metode deep learning", option_d_en: "An animal detection model", option_d_id: "Model deteksi hewan", correct_answer: "A", explanation_en: "HOG counts gradient orientations in local regions. Classic pedestrian detection feature before CNNs.", explanation_id: "HOG menghitung orientasi gradient dalam region lokal. Fitur deteksi pejalan kaki klasik sebelum CNN.", topic: "computer_vision", difficulty: "hard", competition: "noai_prelim"},
  %{question_en: "What is the difference between classification and localization?", question_id: "Apa perbedaan antara klasifikasi dan lokalisasi?", option_a_en: "Classification says what, localization says where in the image", option_a_id: "Klasifikasi mengatakan apa, lokalisasi mengatakan di mana dalam gambar", option_b_en: "They are the same", option_b_id: "Keduanya sama", option_c_en: "Localization is faster", option_c_id: "Lokalisasi lebih cepat", option_d_en: "Classification gives coordinates", option_d_id: "Klasifikasi memberikan koordinat", correct_answer: "A", explanation_en: "Classification outputs class label. Localization adds bounding box coordinates for single object.", explanation_id: "Klasifikasi menghasilkan label kelas. Lokalisasi menambahkan koordinat bounding box untuk satu objek.", topic: "computer_vision", difficulty: "easy", competition: "noai_prelim"},
  %{question_en: "What is Faster R-CNN?", question_id: "Apa itu Faster R-CNN?", option_a_en: "Two-stage detector using Region Proposal Network for efficient detection", option_a_id: "Detektor dua tahap menggunakan Region Proposal Network untuk deteksi efisien", option_b_en: "Faster than any other CNN", option_b_id: "Lebih cepat dari CNN lainnya", option_c_en: "A classification network", option_c_id: "Network klasifikasi", option_d_en: "Real-time detector", option_d_id: "Detektor real-time", correct_answer: "A", explanation_en: "Faster R-CNN uses RPN to propose regions, then classifies each. More accurate but slower than YOLO.", explanation_id: "Faster R-CNN menggunakan RPN untuk mengusulkan region, lalu mengklasifikasi masing-masing. Lebih akurat tapi lebih lambat dari YOLO.", topic: "computer_vision", difficulty: "hard", competition: "noai_prelim"},
  %{question_en: "What is semantic vs instance segmentation?", question_id: "Apa perbedaan segmentasi semantik vs instance?", option_a_en: "Semantic labels pixels by class; instance also distinguishes between individual objects", option_a_id: "Semantik melabeli pixel berdasarkan kelas; instance juga membedakan antara objek individual", option_b_en: "They are the same", option_b_id: "Keduanya sama", option_c_en: "Instance is faster", option_c_id: "Instance lebih cepat", option_d_en: "Semantic separates objects", option_d_id: "Semantik memisahkan objek", correct_answer: "A", explanation_en: "Semantic: all people share 'person' label. Instance: person1, person2, person3 get different IDs.", explanation_id: "Semantik: semua orang berbagi label 'orang'. Instance: orang1, orang2, orang3 mendapat ID berbeda.", topic: "computer_vision", difficulty: "medium", competition: "noai_prelim"},
  %{question_en: "What is panoptic segmentation?", question_id: "Apa itu segmentasi panoptik?", option_a_en: "Combining semantic and instance segmentation into a unified output", option_a_id: "Menggabungkan segmentasi semantik dan instance menjadi output terpadu", option_b_en: "Panoramic images", option_b_id: "Gambar panorama", option_c_en: "All-view segmentation", option_c_id: "Segmentasi semua tampilan", option_d_en: "Optic nerve detection", option_d_id: "Deteksi saraf optik", correct_answer: "A", explanation_en: "Panoptic assigns class to every pixel: countable things get instances, stuff (sky, road) gets semantic labels.", explanation_id: "Panoptik menetapkan kelas ke setiap pixel: benda yang dapat dihitung mendapat instance, stuff (langit, jalan) mendapat label semantik.", topic: "computer_vision", difficulty: "hard", competition: "noai_prelim"},
  %{question_en: "What is image embedding?", question_id: "Apa itu embedding gambar?", option_a_en: "A dense vector representation of an image capturing its visual features", option_a_id: "Representasi vektor padat dari gambar yang menangkap fitur visualnya", option_b_en: "Putting images in a document", option_b_id: "Memasukkan gambar ke dalam dokumen", option_c_en: "Image compression", option_c_id: "Kompresi gambar", option_d_en: "Watermarking", option_d_id: "Watermarking", correct_answer: "A", explanation_en: "Image embeddings are typically from CNN penultimate layer, enabling similarity search and clustering.", explanation_id: "Embedding gambar biasanya dari layer kedua terakhir CNN, memungkinkan pencarian kesamaan dan clustering.", topic: "computer_vision", difficulty: "medium", competition: "noai_prelim"},
  %{question_en: "What is cosine similarity used for in CV?", question_id: "Untuk apa cosine similarity digunakan dalam CV?", option_a_en: "Measuring similarity between image embeddings for retrieval/matching", option_a_id: "Mengukur kesamaan antara embedding gambar untuk retrieval/pencocokan", option_b_en: "Finding image angles", option_b_id: "Menemukan sudut gambar", option_c_en: "Calculating image brightness", option_c_id: "Menghitung kecerahan gambar", option_d_en: "Measuring file sizes", option_d_id: "Mengukur ukuran file", correct_answer: "A", explanation_en: "Cosine similarity compares embedding direction, commonly used in face verification and image search.", explanation_id: "Cosine similarity membandingkan arah embedding, umum digunakan dalam verifikasi wajah dan pencarian gambar.", topic: "computer_vision", difficulty: "medium", competition: "noai_prelim"},
  %{question_en: "What is contrastive learning in CV?", question_id: "Apa itu contrastive learning dalam CV?", option_a_en: "Learning by pulling similar images together and pushing dissimilar ones apart", option_a_id: "Belajar dengan menarik gambar serupa bersama dan mendorong yang berbeda menjauh", option_b_en: "Increasing image contrast", option_b_id: "Meningkatkan kontras gambar", option_c_en: "Comparing colors", option_c_id: "Membandingkan warna", option_d_en: "Contrast adjustment", option_d_id: "Penyesuaian kontras", correct_answer: "A", explanation_en: "Contrastive methods like SimCLR learn visual representations without labels using data augmentation.", explanation_id: "Metode kontrastif seperti SimCLR mempelajari representasi visual tanpa label menggunakan augmentasi data.", topic: "computer_vision", difficulty: "hard", competition: "noai_prelim"},
  %{question_en: "What is the Dice coefficient?", question_id: "Apa itu koefisien Dice?", option_a_en: "A segmentation metric measuring overlap between prediction and ground truth", option_a_id: "Metrik segmentasi yang mengukur overlap antara prediksi dan ground truth", option_b_en: "Random dice rolling", option_b_id: "Pelemparan dadu acak", option_c_en: "Classification accuracy", option_c_id: "Akurasi klasifikasi", option_d_en: "Detection score", option_d_id: "Skor deteksi", correct_answer: "A", explanation_en: "Dice = 2*|A∩B|/(|A|+|B|). Common in medical imaging where pixel-level accuracy matters.", explanation_id: "Dice = 2*|A∩B|/(|A|+|B|). Umum dalam pencitraan medis di mana akurasi tingkat pixel penting.", topic: "computer_vision", difficulty: "medium", competition: "noai_prelim"}
